'use strict';

const fs = require('../fs');
const _require = require('./utils'),
  checkPath = _require.checkPath;
const getMode = options => {
  const defaults = {
    mode: 0o777
  };
  if (typeof options === 'number') return options;
  return {
    ...defaults,
    ...options
  }.mode;
};
module.exports.makeDir = async (dir, options) => {
  checkPath(dir);
  return fs.mkdir(dir, {
